import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const LandingPage = () => {
  const [profile, setProfile] = useState('');
  const [country, setCountry] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleProfileChange = (event) => {
    setProfile(event.target.value);
  };

  const handleCountryChange = (event) => {
    setCountry(event.target.value);
  };

  const handleSubmit = () => {
    if (profile && country) {
      // Store the profile and country info in localStorage
      localStorage.setItem('profile', profile);
      localStorage.setItem('country', country);

      // Redirect to home page
      navigate('/home');
    } else {
      setErrorMessage('Veuillez remplir tous les champs');
    }
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Choisissez votre profil et pays</h1>
      {errorMessage && <p style={styles.errorMessage}>{errorMessage}</p>}

      <div style={styles.formGroup}>
        <label style={styles.label}>Profil:</label>
        <select value={profile} onChange={handleProfileChange} style={styles.select}>
          <option value="local">Local</option>
          <option value="tourist">Touriste</option>
          <option value="professional">Professionnel</option>
        </select>
      </div>

      <div style={styles.formGroup}>
        <label style={styles.label}>Pays:</label>
        <input 
          type="text" 
          placeholder="Entrez votre pays" 
          value={country} 
          onChange={handleCountryChange} 
          style={styles.input}
        />
      </div>

      <button onClick={handleSubmit} style={styles.button}>Soumettre</button>
    </div>
  );
};

const styles = {
  container: {
    padding: '20px',
    backgroundColor: '#0a0a0a',
    color: '#39ff14',
    fontFamily: '"Arial", sans-serif',
    maxWidth: '500px',
    margin: '0 auto',
    textAlign: 'center',
    borderRadius: '10px',
    boxShadow: '0 0 10px rgba(0, 255, 0, 0.3)',
  },
  title: {
    fontSize: '2rem',
    marginBottom: '20px',
    color: '#ffff00',
  },
  formGroup: {
    marginBottom: '20px',
  },
  label: {
    fontSize: '1rem',
    marginBottom: '10px',
    display: 'block',
  },
  select: {
    padding: '10px',
    fontSize: '1rem',
    width: '100%',
    borderRadius: '5px',
    border: '1px solid #39ff14',
    color: '#39ff14',
    backgroundColor: '#0a0a0a',
  },
  input: {
    padding: '10px',
    fontSize: '1rem',
    width: '100%',
    borderRadius: '5px',
    border: '1px solid #39ff14',
    color: '#39ff14',
    backgroundColor: '#0a0a0a',
  },
  button: {
    backgroundColor: '#39ff14',
    color: 'black',
    border: 'none',
    padding: '15px 30px',
    fontSize: '18px',
    cursor: 'pointer',
    borderRadius: '10px',
    transition: 'background-color 0.3s, transform 0.3s',
    boxShadow: '0 4px 6px rgba(0, 255, 0, 0.6)',
  },
  errorMessage: {
    color: 'red',
    fontSize: '1rem',
    marginBottom: '20px',
  },
};

export default LandingPage;
