// src/pages/SearchPage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import AttractionCard from '../components/AttractionCard';

const SearchPage = () => {
  const [filters, setFilters] = useState({ category: '', location: '', priceRange: '' });
  const [attractions, setAttractions] = useState([]);

  const handleFilterChange = (event) => {
    const { name, value } = event.target;
    setFilters(prevFilters => ({ ...prevFilters, [name]: value }));
  };

  useEffect(() => {
    axios.get('API_ENDPOINT', { params: filters })
      .then(response => {
        setAttractions(response.data);
      })
      .catch(error => {
        console.error('Error fetching filtered attractions:', error);
      });
  }, [filters]);

  return (
    <div>
      <h1>Rechercher des attractions</h1>
      <div>
        <label>Cat√©gorie:</label>
        <input type="text" name="category" value={filters.category} onChange={handleFilterChange} />
      </div>
      <div>
        <label>Localisation:</label>
        <input type="text" name="location" value={filters.location} onChange={handleFilterChange} />
      </div>
      <div>
        <label>Plage de prix:</label>
        <input type="text" name="priceRange" value={filters.priceRange} onChange={handleFilterChange} />
      </div>
      <div className="attractions">
        {attractions.map(attraction => (
          <AttractionCard key={attraction.id} attraction={attraction} />
        ))}
      </div>
    </div>
  );
};
const styles = {
  container: {
    padding: '20px',
    backgroundColor: '#f4f4f4',
    color: '#333',
    fontFamily: '"Arial", sans-serif',
    maxWidth: '800px',
    margin: '0 auto',
    borderRadius: '8px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
  },
  title: {
    fontSize: '2rem',
    marginBottom: '20px',
    color: '#39ff14',
    textAlign: 'center',
  },
  filters: {
    marginBottom: '20px',
  },
  formGroup: {
    marginBottom: '15px',
  },
  label: {
    fontSize: '1rem',
    marginBottom: '10px',
    display: 'block',
  },
  select: {
    padding: '10px',
    fontSize: '1rem',
    width: '100%',
    borderRadius: '5px',
    border: '1px solid #39ff14',
    color: '#39ff14',
    backgroundColor: '#fff',
  },
  input: {
    padding: '10px',
    fontSize: '1rem',
    width: '100%',
    borderRadius: '5px',
    border: '1px solid #39ff14',
    color: '#39ff14',
    backgroundColor: '#fff',
  },
  button: {
    backgroundColor: '#39ff14',
    color: 'black',
    border: 'none',
    padding: '15px 30px',
    fontSize: '18px',
    cursor: 'pointer',
    borderRadius: '10px',
    transition: 'background-color 0.3s, transform 0.3s',
  },
  errorMessage: {
    color: 'red',
    fontSize: '1rem',
    marginBottom: '20px',
    textAlign: 'center',
  },
  loading: {
    textAlign: 'center',
    fontSize: '1.2rem',
    color: '#39ff14',
  },
  attractions: {
    display: 'flex',
    flexDirection: 'column',
    gap: '15px',
  },
  noResults: {
    textAlign: 'center',
    fontSize: '1.2rem',
    color: '#333',
  },
};

export default SearchPage;
